import{_ as e,e as o,f as a,V as n}from"./chunks/framework.723ed59e.js";const i=JSON.parse('{"title":"[Login] Account Login Problems","titleTemplate":":title | Kongying Tavern","description":"Troubleshoot","frontmatter":{"titleTemplate":":title | Kongying Tavern","description":"Troubleshoot"},"headers":[],"relativePath":"en/manual/faq/login/accountlogin.md","filePath":"en/manual/faq/login/accountlogin.md","lastUpdated":1688797282000}'),t={name:"en/manual/faq/login/accountlogin.md"},l=[n('<h1 id="login-account-login-problems" tabindex="-1">[Login] Account Login Problems <a class="header-anchor" href="#login-account-login-problems" aria-label="Permalink to &quot;[Login] Account Login Problems&quot;">​</a></h1><ul><li>The map client uses a <span style="color:red;">Gitee account</span>, <span style="color:red;">not your game account</span></li><li>You can register a Gitee account if you don&#39;t have one</li></ul><hr><ul><li>The client is <span style="color:red;">unable to log you in</span> using a <span style="color:red;">phone number</span>, please use your <span style="color:;">linked Email</span> or <span style="color:red;">Gitee username</span> to log in</li></ul><p><img src="/docs/imgs/en/manual/login/giteeusername.png" alt="" loading="lazy"> (You can find your username under your Gitee profile, excluding &quot;@&quot;)</p><hr><p>If you are still unable to log in, please visit <a href="https://gitee.com/" target="_blank" rel="noreferrer">Gitee</a>, and attempt to log in using your linked Email or username. If you cannot log into Gitee properly, please double-check if the username or password is correct.</p><p>If you can log into Gitee properly, while not able to do so in the client, please refer to Known Issue below, and change your password to a mix of numbers and letters only.</p><h3 id="known-issues" tabindex="-1">Known Issues <a class="header-anchor" href="#known-issues" aria-label="Permalink to &quot;Known Issues&quot;">​</a></h3><ul><li>Some special characters in the username or password will prevent you from logging in. (e.g. &quot;&amp;&quot;, &quot;+&quot;, &quot;-&quot;)</li><li>Please change your password if it includes special characters, and use your Email to log in when your username includes special characters.</li><li>Special characters in passwords will prevent the client from saving your password, thus it will request a login at every launch</li><li>If the login issue persists when neither of your username or password includes special characters, it may be caused by a delay in Gitee&#39;s third party login API, please wait for several hours after registration and attempt again</li></ul><hr><p>Reach out in Discord if this issue persists.</p>',12)];const s=e(t,[["render",function(e,n,i,t,s,r){return o(),a("div",null,l)}]]);export{i as __pageData,s as default};
