import{_ as e,e as r,f as a,V as t}from"./chunks/framework.723ed59e.js";const o=JSON.parse('{"title":"[Restore/Recover] Progress Recovery From Stored Data","titleTemplate":":title | Kongying Tavern","description":"Return saves to an archived timestamp, or recover save from accidental deletion.","frontmatter":{"aside":true,"titleTemplate":":title | Kongying Tavern","prev":{"text":"[Overlay Mode] Launching The Game in Windowed Fullscreen (Borderless)","link":"../overlay-mode/fullscreen-windowed/launching"},"description":"Return saves to an archived timestamp, or recover save from accidental deletion.","next":{"text":"Table of Content","link":"en/manual/client-user-manual.md"}},"headers":[],"relativePath":"en/manual/guide/restore-recover/progress.md","filePath":"en/manual/guide/restore-recover/progress.md","lastUpdated":1688797282000}'),i={name:"en/manual/guide/restore-recover/progress.md"},n=[t('<h1 id="restore-recover-progress-recovery-from-stored-data" tabindex="-1">[Restore/Recover] Progress Recovery From Stored Data <a class="header-anchor" href="#restore-recover-progress-recovery-from-stored-data" aria-label="Permalink to &quot;[Restore/Recover] Progress Recovery From Stored Data&quot;">​</a></h1><h3 id="this-feature-requires-a-logged-in-gitee-account-on-your-map-client" tabindex="-1">This feature requires a logged in Gitee account on your map client. <a class="header-anchor" href="#this-feature-requires-a-logged-in-gitee-account-on-your-map-client" aria-label="Permalink to &quot;This feature requires a logged in Gitee account on your map client.&quot;">​</a></h3><h2 id="Return" tabindex="-1">1. Return saves to an archived timestamp <a class="header-anchor" href="#Return" aria-label="Permalink to &quot;1. Return saves to an archived timestamp {#Return}&quot;">​</a></h2><p>Select <strong>Restore</strong> in the <strong>Paimon Menu</strong></p><figure><img src="/docs/imgs/en/manual/restore-recover/1.png" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><p>Select the desired save according to <strong>Timestamp</strong> and <strong>Entries</strong>, you can uncheck the &quot;Current Save Only&quot; option to restore from archives of other saves</p><figure><img src="/docs/imgs/en/manual/restore-recover/2.png" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><figure><img src="/docs/imgs/en/manual/restore-recover/3.png" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><p>Once confirmed, a new save named &quot;Restored Save&quot; will be created to store the restored progress</p><figure><img src="/docs/imgs/en/manual/restore-recover/4.png" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><h2 id="Recovery" tabindex="-1">2. Accidental Save Deletion Recovery <a class="header-anchor" href="#Recovery" aria-label="Permalink to &quot;2. Accidental Save Deletion Recovery {#Recovery}&quot;">​</a></h2><p>Log into <a href="https://gitee.com/" target="_blank" rel="noreferrer">Gitee</a> and select &quot;Create Code&quot;</p><figure><img src="/docs/imgs/en/manual/restore-recover/5.png" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><p>Locate the map client&#39;s cache folder, and select the desired save using &quot;Date Modified&quot;</p><figure><img src="/docs/imgs/en/manual/restore-recover/6.png" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><p>Enter the file contents respectively into the section indicated in the picture below and submit, a save will appear in the list of saves on your map client.</p><figure><img src="/docs/assets/7.f6041527.png" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure>',17)];const s=e(i,[["render",function(e,t,o,i,s,c){return r(),a("div",null,n)}]]);export{o as __pageData,s as default};
