import{_ as a,e as t,f as e,V as i}from"./chunks/framework.723ed59e.js";const n=JSON.parse('{"title":"[Custom Path Planner (Canvas)] Guide","titleTemplate":":title | Kongying Tavern","description":"Basic functionality, import, and export.","frontmatter":{"aside":true,"titleTemplate":":title | Kongying Tavern","next":{"text":"[Easter Egg] View Archived Versions of Golden Apple Archipelago","link":"../easter-egg/view"},"prev":{"text":"[Multi Mark] Instructions","link":"../batch-selection/instructions"},"description":"Basic functionality, import, and export.","banner":"Désolé, la traduction de cette page est toujours en cours"},"headers":[],"relativePath":"fr/manual/guide/canvas/guide.md","filePath":"fr/manual/guide/canvas/guide.md","lastUpdated":1690623855000}'),o={name:"fr/manual/guide/canvas/guide.md"},r=[i('<h1 id="custom-path-planner-canvas-guide" tabindex="-1">[Custom Path Planner (Canvas)] Guide <a class="header-anchor" href="#custom-path-planner-canvas-guide" aria-label="Permalink to &quot;[Custom Path Planner (Canvas)] Guide&quot;">​</a></h1><p>Hotkey summary:</p><ol><li>Left Alt + left click: Create/add a path</li><li>Left Ctrl: Hold to show path anchors</li><li>Double left click: Place an anchor on a path</li><li>Right click on <strong>round anchors</strong>: reset the path curvature at this anchor</li><li>Right click on <strong>square anchors</strong>: delete anchors</li><li>Enter: Conclude editing on the current path</li></ol><hr><h2 id="basic-functionality" tabindex="-1">Basic Functionality <a class="header-anchor" href="#basic-functionality" aria-label="Permalink to &quot;Basic Functionality&quot;">​</a></h2><h3 id="step-1" tabindex="-1"><strong>Step</strong> 1 <a class="header-anchor" href="#step-1" aria-label="Permalink to &quot;**Step** 1&quot;">​</a></h3><p>Click on the map icon located in the upper right</p><figure><img src="/docs/imgs/fr/manual/canvas/1.png" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><h3 id="step-2" tabindex="-1"><strong>Step</strong> 2 <a class="header-anchor" href="#step-2" aria-label="Permalink to &quot;**Step** 2&quot;">​</a></h3><p>Select &quot;Add a New Canvas&quot;</p><figure><img src="/docs/imgs/fr/manual/canvas/2.png" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><h3 id="step-3" tabindex="-1"><strong>Step</strong> 3 <a class="header-anchor" href="#step-3" aria-label="Permalink to &quot;**Step** 3&quot;">​</a></h3><p>Select the newly created canvas (indicated by darkened background)</p><figure><img src="/docs/imgs/fr/manual/canvas/3.png" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><p><img src="/docs/imgs/fr/manual/canvas/4.png" alt="" loading="lazy"> (Canvas under selection, click on it again to deselect)</p><h3 id="step-4" tabindex="-1"><strong>Step</strong> 4 <a class="header-anchor" href="#step-4" aria-label="Permalink to &quot;**Step** 4&quot;">​</a></h3><p>Toggle editing</p><figure><img src="/docs/imgs/fr/manual/canvas/5.png" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><p><img src="/docs/imgs/fr/manual/canvas/6.png" alt="" loading="lazy"> (Picture shows editing toggled ON, click on it again to toggle OFF)</p><h3 id="step-5" tabindex="-1"><strong>Step</strong> 5 <a class="header-anchor" href="#step-5" aria-label="Permalink to &quot;**Step** 5&quot;">​</a></h3><p><strong>Hold Alt and left click</strong> on any location to create a path</p><figure><img src="/docs/imgs/fr/manual/canvas/7.png" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><h3 id="step-6" tabindex="-1"><strong>Step</strong> 6 <a class="header-anchor" href="#step-6" aria-label="Permalink to &quot;**Step** 6&quot;">​</a></h3><p>Holding control allows control over anchors on the path</p><figure><img src="/docs/imgs/fr/manual/canvas/9.png" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><p>At this stage, use <strong>left click</strong> to control the anchors,<br> use <strong>right click</strong> on round anchors to reset path curvature,<br> use <strong>right click</strong> on square anchors to delete it,<br><strong>double click</strong> on the path to place an anchor.</p><h3 id="step-7" tabindex="-1"><strong>Step</strong> 7 <a class="header-anchor" href="#step-7" aria-label="Permalink to &quot;**Step** 7&quot;">​</a></h3><p>Hit <strong>Enter</strong> to conclude editing this path</p><h3 id="step-8" tabindex="-1"><strong>Step</strong> 8 <a class="header-anchor" href="#step-8" aria-label="Permalink to &quot;**Step** 8&quot;">​</a></h3><p>Repeat <strong>Steps</strong> 1 through 7 (note: upon exiting editing via Enter, click on a path to begin editing it)</p><h2 id="export" tabindex="-1">Export <a class="header-anchor" href="#export" aria-label="Permalink to &quot;Export&quot;">​</a></h2><p>Open the Canvas list, select &quot;Export&quot; and save to a desirable directory.</p><figure><img src="/docs/imgs/fr/manual/canvas/10.png" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><figure><img src="/docs/imgs/fr/manual/canvas/11.png" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><figure><img src="/docs/imgs/fr/manual/canvas/12.png" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><h2 id="import" tabindex="-1">Import <a class="header-anchor" href="#import" aria-label="Permalink to &quot;Import&quot;">​</a></h2><p>Open the Canvas list, select &quot;Import&quot; and open the desirable save file.</p><figure><img src="/docs/imgs/fr/manual/canvas/13.png" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><figure><img src="/docs/imgs/fr/manual/canvas/14.png" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><figure><img src="/docs/imgs/fr/manual/canvas/15.png" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure>',40)];const s=a(o,[["render",function(a,i,n,o,s,l){return t(),e("div",null,r)}]]);export{n as __pageData,s as default};
