import{_ as s,e as a,f as l,V as e}from"./chunks/framework.723ed59e.js";const n=JSON.parse('{"title":"【批量选择】使用说明","titleTemplate":":title | 空荧酒馆","description":"应旅行者需求，现已将多选功能加入豪华午餐！","frontmatter":{"aside":false,"titleTemplate":":title | 空荧酒馆","prev":{"text":"【地图性能占用高】前后台帧率设置","link":"../bg/bgfrate"},"next":{"text":"【画板】路线功能教程","link":"../canvas/guide"},"description":"应旅行者需求，现已将多选功能加入豪华午餐！"},"headers":[],"relativePath":"manual/guide/batch-selection/instructions.md","filePath":"manual/guide/batch-selection/instructions.md","lastUpdated":1688793442000}'),t={name:"manual/guide/batch-selection/instructions.md"},r=[e('<h1 id="【批量选择】使用说明" tabindex="-1">【批量选择】使用说明 <a class="header-anchor" href="#【批量选择】使用说明" aria-label="Permalink to &quot;【批量选择】使用说明&quot;">​</a></h1><p>应旅行者需求，现已将多选功能加入豪华午餐！</p><ul><li><span style="color:red;">右键单击</span>某个点位，将直接转换<b><span style="color:green;">完成</span>/<span style="color:red;">未标记</span></b>状态</li><li><span style="color:red;">按住右键划过</span>点位，将直接将划过的点位标记为<b><span style="color:green;">已完成</span></b>状态</li><li>按住<span style="color:red;">左 Alt+右键划过</span>点位，将直接将划过的点位标记为<b><span style="color:red;">未标记</span></b>状态</li><li>按住<span style="color:red;">左 Ctrl+右键框选</span>点位，将直接将框选的所有点位标记为<b><span style="color:green;">已完成</span></b>状态</li><li>按住<span style="color:red;">左 Ctrl+左 Alt+右键框选</span>点位，将直接将框选的所有点位标记为<b><span style="color:red;">未标记</span></b>状态</li></ul><h2 id="instruction" tabindex="-1"><strong>详细说明</strong> <a class="header-anchor" href="#instruction" aria-label="Permalink to &quot;**详细说明** {#instruction}&quot;">​</a></h2><h3 id="s1" tabindex="-1"><strong>多选方式一：点选</strong> <a class="header-anchor" href="#s1" aria-label="Permalink to &quot;**多选方式一：点选** {#s1}&quot;">​</a></h3><ul><li>右键点击<b><span style="color:red;">未完成</span></b>点位，未完成点位将切换为<b><span style="color:green;">已完成</span></b>状态；</li><li>右键点击<b><span style="color:green;">已完成</span></b>点位，未完成点位将切换为<b><span style="color:red;">未完成</span></b>状态。</li></ul><p><img src="https://assets.yuanshen.site/docs/en/manual/1.gif" alt="" loading="lazy"> (右键点选)</p><h3 id="s2" tabindex="-1"><strong>多选方式二：滑动选择</strong> <a class="header-anchor" href="#s2" aria-label="Permalink to &quot;**多选方式二：滑动选择** {#s2}&quot;">​</a></h3><ul><li>按下鼠标右键，滑过相应的点位，<b><span style="color:red;">未完成</span></b>的点位会被标记为<b><span style="color:green;">已完成</span></b>，已完成的点位不会改变。</li></ul><p><img src="https://assets.yuanshen.site/docs/en/manual/2.gif" alt="" loading="lazy"> (右键滑动 - 标记)</p><ul><li>按下左 Alt+鼠标右键，滑过相应点位，<b><span style="color:green;">已完成</span></b>的点位会被标记为<b><span style="color:red;">未完成</span></b>，未完成的点位不会改变。</li></ul><p><img src="https://assets.yuanshen.site/docs/en/manual/3.gif" alt="" loading="lazy"> (右键滑动 - 取消标记)</p><h3 id="s3" tabindex="-1"><strong>多选方式三：框选</strong> <a class="header-anchor" href="#s3" aria-label="Permalink to &quot;**多选方式三：框选** {#s3}&quot;">​</a></h3><ul><li>按下 Ctrl+鼠标右键，开始拖动，松开鼠标右键后，选框中<b><span style="color:red;">未完成</span></b>的点位会被标记为<b><span style="color:green;">已完成</span></b>，已完成的点位不会改变。</li></ul><p><img src="https://assets.yuanshen.site/docs/en/manual/4.gif" alt="" loading="lazy"> (右键框选 - 标记)</p><ul><li>按下 Ctrl+左 Alt+鼠标右键，开始拖动，松开鼠标右键后，选框中<b><span style="color:red;">未完成</span></b>的点位会被标记为<b><span style="color:green;">已完成</span></b>，未完成的点位不会改变。</li></ul><p><img src="https://assets.yuanshen.site/docs/en/manual/5.gif" alt="" loading="lazy"> (右键框选 - 取消标记)</p>',17)];const o=s(t,[["render",function(s,e,n,t,o,i){return a(),l("div",null,r)}]]);export{n as __pageData,o as default};
