import{_ as a,e as i,f as e,V as t}from"./chunks/framework.723ed59e.js";const n=JSON.parse('{"title":"【画板】路线功能教程","titleTemplate":":title | 空荧酒馆","description":"基础使用教程。","frontmatter":{"aside":true,"titleTemplate":":title | 空荧酒馆","next":{"text":"【彩蛋】查看历史版本金苹果群岛","link":"../easter-egg/view"},"prev":{"text":"【批量选择】使用说明","link":"../batch-selection/instructions"},"description":"基础使用教程。"},"headers":[],"relativePath":"manual/guide/canvas/guide.md","filePath":"manual/guide/canvas/guide.md","lastUpdated":1688793442000}'),l={name:"manual/guide/canvas/guide.md"},s=[t('<h1 id="【画板】路线功能教程" tabindex="-1">【画板】路线功能教程 <a class="header-anchor" href="#【画板】路线功能教程" aria-label="Permalink to &quot;【画板】路线功能教程&quot;">​</a></h1><p>快捷键说明：</p><ol><li>左 Alt+鼠标左键：新建/添加线段</li><li>左 Ctrl：按住显示控制节点</li><li>左键双击线条：在此处插入新节点</li><li>右键点击【圆形节点】：初始化该节点弧度为线性</li><li>右键点击【方形节点】：删除此节点</li><li>回车：完成该线条编辑</li></ol><hr><h2 id="基础使用教程" tabindex="-1">基础使用教程 <a class="header-anchor" href="#基础使用教程" aria-label="Permalink to &quot;基础使用教程&quot;">​</a></h2><h3 id="step1" tabindex="-1">第一步 <a class="header-anchor" href="#step1" aria-label="Permalink to &quot;第一步 {#step1}&quot;">​</a></h3><p>点击右上角打开画板</p><figure><img src="/docs/imgs/manual/canvas/1.png" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><h3 id="step2" tabindex="-1">第二步 <a class="header-anchor" href="#step2" aria-label="Permalink to &quot;第二步 {#step2}&quot;">​</a></h3><p>点击添加一个空画布</p><figure><img src="/docs/imgs/manual/canvas/2.png" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><h3 id="step3" tabindex="-1">第三步 <a class="header-anchor" href="#step3" aria-label="Permalink to &quot;第三步 {#step3}&quot;">​</a></h3><p>选中新的画布（黑色背景为被选中状态）</p><p><img src="/docs/imgs/manual/canvas/3.png" alt="" loading="lazy"> （此为未选中状态）</p><p><img src="/docs/imgs/manual/canvas/4.png" alt="" loading="lazy"> （此为选中状态，再次点击可隐藏）</p><h3 id="step4" tabindex="-1">第四步 <a class="header-anchor" href="#step4" aria-label="Permalink to &quot;第四步 {#step4}&quot;">​</a></h3><p>打开编辑模式</p><figure><img src="/docs/imgs/manual/canvas/5.png" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><p><img src="/docs/imgs/manual/canvas/6.png" alt="" loading="lazy"> （此为开启状态，再次点击关闭）</p><h3 id="step5" tabindex="-1">第五步 <a class="header-anchor" href="#step5" aria-label="Permalink to &quot;第五步 {#step5}&quot;">​</a></h3><p>在任意位置【按住 alt】并【鼠标左键点击】即可生成线条</p><figure><img src="/docs/imgs/manual/canvas/7.png" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><p>按住 ctrl 可显示控制节点</p><figure><img src="/docs/imgs/manual/canvas/9.png" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><h3 id="step6" tabindex="-1">第六步 <a class="header-anchor" href="#step6" aria-label="Permalink to &quot;第六步 {#step6}&quot;">​</a></h3><p>此时可以【鼠标左键节点】进行点操作，</p><p>【右键圆形节点】可以使该点弧度初始化，</p><p>【右键方形节点】可删除此节点，</p><p>在线条上【双击鼠标左键】可插入节点。</p><h3 id="step7" tabindex="-1">第七步 <a class="header-anchor" href="#step7" aria-label="Permalink to &quot;第七步 {#step7}&quot;">​</a></h3><p>敲回车键完成此线条编辑</p><h3 id="step8" tabindex="-1">第八步 <a class="header-anchor" href="#step8" aria-label="Permalink to &quot;第八步 {#step8}&quot;">​</a></h3><p>重复第一步—第七步。</p><p>（PS：完成编辑后，鼠标左键选中任意一条线进入该线条编辑）</p><h2 id="export" tabindex="-1">导出教程 <a class="header-anchor" href="#export" aria-label="Permalink to &quot;导出教程 {#export}&quot;">​</a></h2><p>打开面板点击导出此画布，选择位置保存</p><figure><img src="/docs/imgs/manual/canvas/10.png" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><figure><img src="/docs/imgs/manual/canvas/11.png" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><figure><img src="/docs/imgs/manual/canvas/12.png" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><h2 id="import" tabindex="-1">导入教程 <a class="header-anchor" href="#import" aria-label="Permalink to &quot;导入教程 {#import}&quot;">​</a></h2><p>打开面板选择导入画布，可导入选择的画布</p><figure><img src="/docs/imgs/manual/canvas/13.png" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><figure><img src="/docs/imgs/manual/canvas/14.png" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><figure><img src="/docs/imgs/manual/canvas/15.png" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure>',44)];const r=a(l,[["render",function(a,t,n,l,r,o){return i(),e("div",null,s)}]]);export{n as __pageData,r as default};
